import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

import Setting from "../component/setting";
import Messages from "../component/message";

import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import {
  Grid,
  Stack,
  Typography,
  Divider,
  Drawer,
  Avatar,
  Paper,
} from "@mui/material";
import { Box } from "@mui/system";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import MailIcon from "@mui/icons-material/Mail";
import MenuIcon from "@mui/icons-material/Menu";
import InboxIcon from "@mui/icons-material/MoveToInbox";
import DashboardPart from "../component/dashboardPart"

import adjustPaper from "../common/adjustPaperfiile";
import SimplePaper from "../common/paper";

import SearchAppBar from "../common/appBar";
function Dashboard(props) {
  const { state } = useLocation();

  const navigate = useNavigate();

  const navigatedynamically = (path) => {
    navigate(`${text}`, { state: { name: "abc" } });
  };
  const list = (anchor) => (
    <Box>
      <List>
        {["Inbox", "Starred", "Send email", "Drafts"].map((text, index) => (
          <ListItem key={text} disablePadding>
            {/* <Link to ="/show"> */}
            <ListItemButton onClick={() => {navigatedynamically(text)}}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
            {/* </Link> */}
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {["All mail", "Trash", "Spam"].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <div>
      <Grid container>
        <Grid
          item
          xs={0}
          sm={0}
          lg={0}
          xl={3}
          padding={1}
          sx={{ display: { xs: "none", sm: "block" } }}
        >
          <Box sx={{ border: "1px solid" }}>
            <Typography variant="h3" align="center">
              Dashboard
            </Typography>
            {list()}
          </Box>
        </Grid>

      {/* <Routes>
        <Route path = "/inbox" component ={<Inbox/>} /> 
        <Route path = "/inbox" component ={<Inbox/>} /> 
        <Route path = "/inbox" component ={<Inbox/>} /> 
        <Route path = "/inbox" component ={<Inbox/>} /> 
        <Route path = "/inbox" component ={<Inbox/>} /> 
      </Routes> */}

        <DashboardPart/>
        {/* <Grid item xs={12} sm={12} lg={12} xl={9} padding={1}>
          <Box sx={{ border: "1px solid" }}>
            <Grid container>
              <Grid item xs={8} sm={8} lg={6} xl={8}>
                <Stack spacing={3}>
                  <SearchAppBar />
                  <Stack direction="row" spacing={3} padding={1}>
                    <Avatar
                      alt="Travis Howard"
                      src="https://mui.com/static/images/avatar/1.jpg"
                    />
                    <Box>
                      <Typography variant="h5">
                        Hello, Mr {state.email}{" "}
                      </Typography>
                      <Typography variant="body1">
                        Check your activities in the dashboard{" "}
                      </Typography>
                    </Box>
                  </Stack>

                  <SimplePaper />

                  <Box>
                    <Paper sx={{ width: "97%", margin: "auto" }}>
                      <img
                        style={{ width: "100%" }}
                        src="https://image.shutterstock.com/image-photo/diagrams-projecting-tablet-600w-1276620463.jpg"
                      />
                    </Paper>
                  </Box>

                  <Box>
                    <Paper
                      sx={{ width: "97%", height: "14vh", margin: "auto" }}
                    >
                      fsdf
                    </Paper>
                  </Box>
                </Stack>
              </Grid>
              <Grid item xs={12} sm={12} lg={12} xl={4} padding={1}>
                <Stack spacing={5}>
                  <SimplePaper />
                  <SimplePaper />
                  <SimplePaper />
                  <SimplePaper />
                  <SimplePaper />
                </Stack>
              </Grid>
            </Grid>
          </Box>
        </Grid> */}
      </Grid>

      {/* <Routes>
        <Route path ="/show" element ={<SimplePaper/>} />
         <Route path="/setting" element={<Setting />}/>
      </Routes> */}

      <h1>Welcome {state.email}</h1>
      <Routes>
        <Route path="/messages" element={<Messages />} />
      </Routes>
    </div>
  );
}

export default Dashboard;
