import React from "react";
import { useLocation } from "react-router-dom";

import Setting from "../component/setting";
import Messages from "../component/message";

import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { Grid, Stack, Typography,Divider ,Drawer,Avatar} from "@mui/material";
import { Box } from "@mui/system";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import MenuIcon from '@mui/icons-material/Menu';
import InboxIcon from '@mui/icons-material/MoveToInbox';


import SearchAppBar from "../common/appBar"
function Dashboard(props) {
  const { state } = useLocation();

  const list = (anchor) => (
    <Box >
      <List>
        {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <div>
      <Grid container>
        <Grid
          xs={0}
          sm={0}
          lg={0}
          xl={3}
          padding={1}
          sx={{ display: { xs: "none", lg: "block" } }}
        >
          <Box sx={{ border: "1px solid" }}>
            <Typography variant="h3" align="center">Dashboard here</Typography>
                 {list()}
          </Box>
        </Grid>
        <Grid xs={12} sm={12} lg={12} xl={9} padding={1}>
          <Box sx={{ border: "1px solid" }}>
              <Grid container>
                    <Grid xs ={8} sm ={8} lg ={6} xl = {8}>
                            <SearchAppBar/>   
                            <Stack direaction = {"row"}>
                            <Avatar alt="Travis Howard" src="https://mui.com/static/images/avatar/1.jpg" />
                            <Typography variant="h5">Hello, Mr {state.email} </Typography>
                            <Typography variant="subtitle2">Check your activities in the dashboard </Typography>
                            </Stack>
                            
                    </Grid>          


                  </Grid> 

          </Box>
        </Grid>
      </Grid>










      <h1>Welcome {state.email}</h1>
      <Routes>
        <Route path="/setting" element={<Setting />}>
          {" "}
        </Route>
        <Route path="/messages" element={<Messages />}>
          {" "}
        </Route>
      </Routes>
    </div>
  );
}

export default Dashboard;
