import React from "react";
import _ from "lodash";

function Pagination({pageSize, count, displayData}) {
  const paginationArr = count.length / pageSize;
  const arrPagination = _.range(1, paginationArr);
  return (
    <nav aria-label="...">
      <ul class="pagination pagination-lg">
        {arrPagination.map((item) => (
          <li class="page-item disabled" onClick={() => {displayData(pageSize,count,item)}}>
            <a class="page-link" href="#" tabindex="-1" >
              {item}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Pagination;
