import React from 'react';
import { useLocation } from 'react-router-dom';

import Setting from '../component/setting';
import Messages from '../component/message';


import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
  } from "react-router-dom";
import { Grid, Stack, Typography } from '@mui/material';
import { Box } from '@mui/system';




function Dashboard(props) {
    const {state} = useLocation() 
    return (
        <div>

                {/* <Stack direction='row' spacing={2}>
                    <Box width="20%" sx={{border:"1px solid",display:{xs:"none",sm:"block"}}}>
                        <h1>List herer</h1>

                    </Box>
                    <Box width="80%" sx={{border:"1px solid"}}>
                        <h1>dashboard herer</h1>

                    </Box>

                    
                </Stack> */}


             <Grid container>
                 <Grid xs={0} sm ={0} lg={0} xl ={3} padding={1} sx ={{display:{xs:"none",lg:"block"}}}>
                 <Typography variant='h2' sx ={{border:"1px solid"}}>List herer</Typography>
                 </Grid>
                 <Grid xs={12} sm ={12} lg={12} xl ={8} sx ={{border:"1px solid"}}>
                            <h1>Dashboard</h1>
                 </Grid>
                 
                 
              </Grid>   



                <h1>Welcome {state.email}</h1>
                <Routes>
                    <Route path='/setting' element={<Setting/>} >  </Route>
                    <Route path='/messages' element={<Messages/>} >  </Route>
                </Routes>
        </div>
    );
}

export default Dashboard;
