import React from 'react';
import { Typography,Grid, TextField, Stack} from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import { Box } from '@mui/system';
import Button from '@mui/material/Button';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';




function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }
  
  const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
  ];






function Todo(props) {
    return (
        <div>
            <Grid container>
                 <Grid sx={{margin:"auto"}} item xs = {10} sm = {10} md = {6} lg = {6} xl ={6}>
                    <Stack spacing={2}>

                   <Box sx={{border:"1px solid " }}>
                    <Box padding ={2} >
                        <Typography>Add A Task</Typography>
                    </Box>
                    <Divider />
                    <Box padding={2}>
                    <Stack spacing={2}>
                          <Typography variant ="h6">Add A Task</Typography>
                          <TextField id="outlined-basic" label="Outlined" variant="outlined"  />
                          <Typography variant ="body1">Enter what you want to submits</Typography>
                        <Button variant="contained" sx={{width:"15%"}}>Send</Button>
                    </Stack>
                    <Box>
                        {/* <Button variant ="contained" >submit</Button> */}
                    </Box>
                    </Box>
                    </Box>  

                    <Box>
                    <Box padding ={2}  sx={{border:"1px solid " }} >
                        <Typography>Add A Task</Typography>
                    </Box>
                    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
            <TableCell align="right">Protein&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
              <TableCell align="right">{row.protein}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>


                    </Box>
                    </Stack>



                 </Grid>
            </Grid>
        </div>
    );
}

export default Todo;
